mapper.select:
		select * 
			from (select rownum rnum, a.* 
        	  from (select * 
        	  		from tbl_notice	order by not_num desc
                   ) a
             )
		where rnum >= #{startRow} and rnum <= #{endRow}

mapper.update:
update tbl_notice set
	not_title = #{not_title},
	not_content = #{not_content}
where not_num = #{not_num}

update tbl_notice set
	not_viewcount = not_viewcount + 1
where not_num = #{not_num}
