mapper.select:
select count(*)
from tbl_product
where pdt_date=1;

select count(*)
from tbl_product
where pdt_date>1;

		select * 
			from (select rownum rnum, a.* 
        	  from (select * from tbl_product
					order by pdt_num desc
                 ) a
             )
		where rnum >= #{startRow} and rnum <= #{endRow}

mapper.update:
update tbl_product set
pdt_sales = pdt_sales + #{pdt_sales}
where pdt_num = #{pdt_num}
