mapper.select:
		select * 
			from (select rownum rnum, a.* 
        	  from (select * 
        	  		from tbl_qna order by qna_ref desc, qna_restep desc
                   ) a
             )
		where rnum >= #{startRow} and rnum <= #{endRow}

mapper.update:
update tbl_qna set
	qna_success = #{qna_writer}
where qna_num = #{qna_ref}
